package com.titan.common.constants;/** * @author: yupu * @Date: Created in 上午10:36 2018/9/29 * @Des: * @Modifyed By: */public interface ErrorCode {    /**     * 用户中心错误码     */    enum Center implements IErrMessage{        AUTHENTICATION_ERROR(3001, "鉴权失败"),        NOT_AUTHENTICATION_ERROR(3002, "未鉴权")        ;        private int errCode;        private String errMessage;        Center(int errCode,String errMessage){            this.errCode = errCode;            this.errMessage = errMessage;        }        @Override        public int getErrCode() {            return this.errCode;        }        @Override        public String getErrMessage() {            return this.errMessage;        }    }    /**     * 主要进行大数据分析、挖掘、敏感词匹配错误码     */    enum Analysis implements IErrMessage{        ;        @Override        public int getErrCode() {            return 0;        }        @Override        public String getErrMessage() {            return null;        }    }}