package com.titan.common.result;/** * @author: yupu * @Date: Created in 上午10:23 2018/9/29 * @Des： * @Modifyed By: */public abstract class BaseResult<T> {    private int code = 1000;    private String message;    private T data;    public int getCode() {        return code;    }    public void setCode(int code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }}